package org.rudi.microservice.kalim.service.integration.impl.transformer;

import java.util.Arrays;
import java.util.List;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;
import org.rudi.facet.kaccess.bean.Language;
import org.rudi.facet.kaccess.bean.RichDictionaryEntry;
import org.rudi.microservice.kalim.service.integration.impl.transformer.metadata.SummaryFormatMetadataTransformer;

import static org.junit.Assert.assertNull;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

class SummaryFormatMetadataTransformerUT {

	private static final char SEPARATOR = '|';
	private final SummaryFormatMetadataTransformer transformer = new SummaryFormatMetadataTransformer();

	@Test
	void testTranform_EmptyList() {
		List<RichDictionaryEntry> result = transformer.tranform(Arrays.asList());
		assertTrue(result.isEmpty());
	}

	@Test
	void testTranform_NullList() {
		List<RichDictionaryEntry> result = transformer.tranform(null);
		assertTrue(result.isEmpty());
	}

	@Test
	void testTranform_NullOrEmptyText() {
		RichDictionaryEntry entryEmpty = new RichDictionaryEntry();
		entryEmpty.setLang(Language.EN);
		entryEmpty.setHtml("");

		RichDictionaryEntry entryNull = new RichDictionaryEntry();
		entryNull.setLang(Language.FR);
		entryNull.setHtml(null);

		List<RichDictionaryEntry> result = transformer.tranform(Arrays.asList(entryEmpty, entryNull));
		assertEquals("", result.get(0).getHtml());
		assertNull(result.get(1).getHtml());
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<b>Hello</b> <script>alert('xss')</script>World" + SEPARATOR + "1<b>Hello</b> World2" + SEPARATOR + "1Hello World2",
		"<script>alert('XSS')</script>" + SEPARATOR + "12"+ SEPARATOR + "12",
		"<svg onload=\"alert('XSS')\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"<iframe src=\"javascript:alert('XSS');\"></iframe>" + SEPARATOR + "12" + SEPARATOR + "12",
		"<body onload=\"alert('XSS')\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"<input type=\"text\" value=\"XSS\" onfocus=\"alert('XSS')\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"<math href=\"javascript:alert('XSS')\"></math>" + SEPARATOR + "12" + SEPARATOR + "12",
		"<SCRIPT/XSS SRC=\"http://xss.rocks/xss.js\"></SCRIPT>" + SEPARATOR + "12" + SEPARATOR + "12",
		"<INPUT TYPE=\"IMAGE\" SRC=\"javascript:alert('XSS');\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"<BODY ONLOAD=alert('XSS')>" + SEPARATOR + "12" + SEPARATOR + "12",
		"<IFRAME SRC=# onmouseover=\"alert(document.cookie)\"></IFRAME>" + SEPARATOR + "12" + SEPARATOR + "12",
		"<FRAMESET><FRAME SRC=\"javascript:alert('XSS');\"></FRAMESET>" + SEPARATOR + "12" + SEPARATOR + "12",
		"<META HTTP-EQUIV=\"Set-Cookie\" Content=\"USERID=<SCRIPT>alert('XSS')</SCRIPT>\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"a\"><script>alert('XSS')</script>" + SEPARATOR + "1a\"&gt;2" + SEPARATOR + "1a\"&gt;2",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranform_removesScript(String inputText, String expectedHtml, String expectedText) {
		transformAndAssert("1" + inputText + "2", expectedHtml, expectedText);
	}

	@Test
	void testTransform_br() {
		String inputText = "<BR SIZE=\"&{alert('XSS')}\">";
		String expectedHtml = "<br>";
		String expectedText = "";
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<a href=\"http://www.example.com\">Click me</a>" + SEPARATOR + "<a href=\"http://www.example.com\" rel=\"nofollow\" target=\"_blank\">Click me</a>" + SEPARATOR + "Click me",
		"<a href=\"https://www.bigbuckbunny.org/\" target=\"_blank\" rel=\"nofollow\">Big Buck Bunny</a>" + 
				SEPARATOR + "<a href=\"https://www.bigbuckbunny.org/\" target=\"_blank\" rel=\"nofollow\">Big Buck Bunny</a>" + SEPARATOR + "Big Buck Bunny",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranform_validHref(String inputText, String expectedHtml, String expectedText) {
		// forcage du rel="nofollow" et target="_blank"
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<a href=\"javascript:alert('XSS')\">Click me</a>" + SEPARATOR + "<a rel=\"nofollow\" target=\"_blank\">Click me</a>" + SEPARATOR + "Click me",
		"<a href=\"&#106;&#97;&#118;&#97;&#115;&#99;&#114;&#105;&#112;&#116;&#58;&#97;&#108;&#101;&#114;&#116;&#40;&#39;&#88;&#83;&#83;&#39;&#41;\">Click me</a>" 
				+ SEPARATOR + "<a rel=\"nofollow\" target=\"_blank\">Click me</a>" + SEPARATOR + "Click me",
		"<a href=\"&#0000106&#0000097&#0000118&#0000097&#0000115&#0000099&#0000114&#0000105&#0000112&#0000116&#0000058&#0000097&#0000108&#0000101&#0000114&#0000116&#0000040&#0000039&#0000088&#0000083&#0000083&#0000039&#0000041\">Click me</a>" 
				+ SEPARATOR + "<a rel=\"nofollow\" target=\"_blank\">Click me</a>" + SEPARATOR + "Click me",
		"<a href=\"&#x6A&#x61&#x76&#x61&#x73&#x63&#x72&#x69&#x70&#x74&#x3A&#x61&#x6C&#x65&#x72&#x74&#x28&#x27&#x58&#x53&#x53&#x27&#x29\">Click me</a>" 
				+ SEPARATOR + "<a rel=\"nofollow\" target=\"_blank\">Click me</a>" + SEPARATOR + "Click me",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranform_invalidHref(String inputText, String expectedHtml, String expectedText) {
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<i><b>a<style=\"color: red;\">Red Text</style>b</b></i>" + SEPARATOR + "<i><b>aRed Textb</b></i>" + SEPARATOR + "aRed Textb",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranform_invalidStyle(String inputText, String expectedHtml, String expectedText) {
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<p align=\"right\">text</p>" + SEPARATOR + "<p align=\"right\">text</p>" + SEPARATOR + "text",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranform_pWithStyle(String inputText, String expectedHtml, String expectedText) {
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<div style=\"background-image:url(javascript:alert('XSS'))\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"<div></div>" + SEPARATOR + "12" + SEPARATOR + "12",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranformInvalidTag_div(String inputText, String expectedHtml, String expectedText) {
		transformAndAssert("1" + inputText + "2", expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<TABLE><TD BACKGROUND=\"javascript:alert('XSS')\">" + SEPARATOR + "12" + SEPARATOR + "12",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranformInvalidTag_table(String inputText, String expectedHtml, String expectedText) {
		// tag <table> non supporté
		transformAndAssert("1" + inputText + "2", expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
			"<figure><img src=\"image.jpg\" alt=\"Image\"><figcaption>Caption</figcaption></figure>" + SEPARATOR + "Caption" + SEPARATOR + "Caption",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTransformInvalidTag_figure(String inputText, String expectedHtml, String expectedText) {
		// tag <figure> non supporté
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	@Test
	void testTransformInvalidTag_imgBinaire() {
		String inputText = "<img style=\"aspect-ratio:728/546;\" src=\"data:image/jpeg;base64,\" width=\"728\" height=\"546\"/>";
		String expectedHtml = "12";
		String expectedText = "12";
		// tag img non autorisé
		transformAndAssert("1" + inputText + "2", expectedHtml, expectedText);
	}

	@ParameterizedTest
	@CsvSource(value = {
	//@formatter:off
		"<img src=\"x\" onerror=\"alert('XSS')\">" + SEPARATOR + "12" + SEPARATOR + "12",
		"a<script>alert('XSS')</script>b<img src=\"x\" onerror=\"alert('XSS')\">c" + SEPARATOR + "1abc2"+ SEPARATOR + "1abc2",
		//@formatter:on
	}, delimiter = SEPARATOR)
	void testTranformInvalidTag_img(String inputText, String expectedHtml, String expectedText) {
		transformAndAssert("1" + inputText + "2", expectedHtml, expectedText);
	}

	@Test
	void testTransformInvalidTag_video() {
		String inputText = "<!DOCTYPE html> \r\n" + "<html> \r\n" + "<body>\r\n" + " \r\n"
				+ "<video width=\"400\" controls>\r\n" + "<source src=\"mov_bbb.mp4\" type=\"video/mp4\">\r\n"
				+ "<source src=\"mov_bbb.ogg\" type=\"video/ogg\">\r\n"
				+ "  Your browser does not support HTML video.\r\n" + "</video>" + "</body> \r\n" + "</html>";
		String expectedHtml = "Your browser does not support HTML video.";
		String expectedText = "Your browser does not support HTML video.";
		// tag video non autorisé
		transformAndAssert(inputText, expectedHtml, expectedText);
	}

	private void transformAndAssert(String inputText, String expectedHtml, String expectedText) {
		RichDictionaryEntry entry = new RichDictionaryEntry();
		entry.setLang(Language.EN);
		entry.setText(inputText);
		entry.setHtml(inputText);

		List<RichDictionaryEntry> input = Arrays.asList(entry);
		List<RichDictionaryEntry> result = transformer.tranform(input);

		RichDictionaryEntry transformed = result.get(0);
		assertEquals(Language.EN, transformed.getLang());
		assertEquals(expectedHtml, transformed.getHtml());
		assertEquals(expectedText, transformed.getText());

	}

}
